const input1 = document.querySelectorAll('#containerDataUsername input');
const select1 = document.querySelectorAll('#containerDataUsername select');

const lastInput1 = input1[input1.length - 1];
const lastSelect1 = select1[select1.length - 1];

input1.forEach(e => {
    e.addEventListener('input', () => {
    if( e.required && !e.value && !lastInput1.value && !lastSelect1.value ) {
        btnSelanjutnya1.classList.add('disabled');
    }else if( e.required && e.value && lastInput1.value && lastSelect1.value) {
        btnSelanjutnya1.classList.remove('disabled');
    }
    })

})

