const input5 = document.querySelectorAll('#containerDataBerkas input');
const lastInput5 = input5[input5.length - 1];
const buttonSubmit = document.querySelector('#containerDataBerkas button[type=submit]');

input5.forEach(e => {
    e.addEventListener('input', () => {
    if( e.required && !e.value && !lastInput5.value ) {
        buttonSubmit.setAttribute('disabled', '');
    }else if( e.required && e.value && lastInput5.value ) {
        buttonSubmit.removeAttribute('disabled');
    }
    })

})

function ubahJenjangPendidikan() {
    const selectNull = document.getElementById('jenjang_id').value;
    const label = document.getElementById('labelJenjang');

    if( !selectNull == "") {
    label.style.color = '#000';
    }

}

